import {useAuthStatus} from '../hooks/useAuthStatus';
import styles from '../styles/Dashboard.module.css';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useState } from 'react';
import { response } from '../../../backend/server1';

function Dashboard() {
    const [targetEmail,setTargetEmail] = useState("");
    const {isLoading, isAuthorized, username} = useAuthStatus();

    console.log(isAuthorized);

    const sendMagicLink = async (event)  => {
        event.preventDefault()
        fetch("/api/triggerMagic", {
            method: "post",
            headers:{
                "Content-Type": "application/json"
            },
            body: {"targetEmail": targetEmail}
        }).then(response=> response.json()).then(data => {
            console.log(data);
        })
        //window.location.href='/magicLinkMessage'
        // let magicLink = await passage.createMagicLink({
        //     email: "karasani.tejaswi01@gmail.com",
        //     redirect_url: "/magicLinkMessage" ,
        //     magic_link_path: "/magicLinkMessage",
        //     send: true,
        //     channel: "email"
        //   });

        //   console.log(magicLink.url)
    }

    if (isLoading) {
        return null;
    }
    const authorizedBody = 
    <>
        You successfully signed in with Passage.
        <br/><br/>
        Your username is: <b>{username}</b>
    </>

    const unauthorizedBody = 
    <>
        You have not logged in and cannot view the dashboard.
        <br/><br/>
        <a href="/" className={styles.link}>Login to continue.</a>
    </>

    return (
        <div className={styles.dashboard}>
            <div className={styles.title}>{isAuthorized ? 'Welcome!' : 'Unauthorized'}</div>
            <div className={styles.message}>
                { isAuthorized ? authorizedBody : unauthorizedBody }
            </div>
            {isAuthorized?
            <div><TextField id="outlined-basic" label="Outlined" variant="outlined" onChange ={(e) => setTargetEmail(e.target.value)}/>
            <Button variant="contained" onClick={(event) => sendMagicLink(event)}>Send</Button></div>:null
            }
        </div>
    );

}

export default Dashboard;
